      {!directoryHandle ? (
        <div>
          <button 
            onClick={handleSelectDirectory}
            disabled={isSelecting}
            className="select-button"
          >
            {isSelecting ? 'Selecting...' : 'Select Workflow Directory'}
          </button>
          {error && (
